function isFirefox(){return navigator.userAgent.search("Firefox")>0}function shouldReloadVideosAsWorkaroundForStalledConnection(){return!0}function fetchAndReload(e,t){fetch(e).then(e=>t.load())}function reloadSource(e,t){const n=e.getAttribute("src");fetchAndReload(n,t)}function reloadSources(e){const n=e.getElementsByTagName("source");for(var t=0;t<n.length;t++)reloadSource(n[t],e)}function reloadVideo(e){if(e.hasAttribute("src")){const t=e;reloadSource(t,e)}reloadSources(e)}function reloadVideos(){const t=document.getElementsByTagName("video");for(var e=0;e<t.length;e++)reloadVideo(t[e])}shouldReloadVideosAsWorkaroundForStalledConnection()&&window.addEventListener("load",e=>reloadVideos())